  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

/* Habit tracker animations */
@keyframes habit-check {
  0% { transform: scale(1); }
  50% { transform: scale(1.2); }
  100% { transform: scale(1); }
}

@keyframes habit-glow {
  0% { box-shadow: 0 0 0 0 var(--primary); }
  50% { box-shadow: 0 0 12px 2px var(--primary); }
  100% { box-shadow: 0 0 0 0 var(--primary); }
}

@keyframes fade-in {
  from { opacity: 0; transform: translateY(8px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes slide-in {
  from { opacity: 0; transform: translateX(-12px); }
  to { opacity: 1; transform: translateX(0); }
}

.animate-habit-check {
  animation: habit-check 0.3s ease-out;
}

.animate-habit-glow {
  animation: habit-glow 0.5s ease-out;
}

.animate-fade-in {
  animation: fade-in 0.4s ease-out forwards;
}

.animate-slide-in {
  animation: slide-in 0.3s ease-out forwards;
}

/* Heatmap cell styles */
.heatmap-cell {
  transition: all 0.15s ease;
}

.heatmap-cell:hover {
  transform: scale(1.3);
  z-index: 10;
}

/* Category badge styles */
.category-health { background-color: var(--category-health); }
.category-productivity { background-color: var(--category-productivity); }
.category-mindfulness { background-color: var(--category-mindfulness); }
.category-fitness { background-color: var(--category-fitness); }
.category-learning { background-color: var(--category-learning); }

